mapper.insert:
INSERT INTO MENU
    (
      menu_name
    , menu_url
    , parent_menu_id
    , sort_order
    , usage_status
    , created_by
    , created_at
    ) VALUES (
      #{menuName}
    , #{menuUrl}
    , #{parentMenuId}
    , #{sortOrder}
    , #{usageStatus}
    , #{userId}
    , NOW()
    )

mapper.update:
UPDATE MENU
   SET MENU_NAME        = #{menuName}
     , MENU_URL         = #{menuUrl}
     , PARENT_MENU_ID   = #{parentMenuId}
     , SORT_ORDER       = #{sortOrder}
     , USAGE_STATUS     = #{usageStatus}
     , MODIFIED_BY      = #{userId}
     , MODIFIED_AT      = NOW()
 WHERE ID = #{menuId}
