mapper.insert:
INSERT INTO `menu`
    (
      menu_name
    , menu_url
    , parent_menu_id
    , sort_order
    , usage_status
    , is_displayed
    , icon
    , created_by
    , created_at
    ) VALUES (
      #{menuName}
    , #{menuUrl}
    , #{parentMenuId}
    , #{sortOrder}
    , #{usageStatus}
    , #{isDisplayed}
    , #{icon}
    , #{userId}
    , NOW()
    )

mapper.update:
UPDATE `menu`


      , menu_name        = #{menuName}


      , menu_url         = #{menuUrl}

      , parent_menu_id   = #{parentMenuId}

      , sort_order       = #{sortOrder}


      , usage_status     = #{usageStatus}


      , is_displayed     = #{isDisplayed}


      , icon             = #{icon}

      , modified_by      = #{userId}
      , modified_at      = NOW()

 WHERE id = #{menuId}
