mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY USER_ID) AS rowNumber , USER_ID AS userId , USER_NAME AS userName , EMAIL AS email , CREATED_BY AS createdBy , DATE_FORMAT(CREATED_AT, '%Y-%m-%d') AS createdAt FROM USER
AND USER_NAME LIKE CONCAT('%', #{userName}, '%')
AND EMAIL LIKE CONCAT('%', #{email}, '%')
AND CREATED_AT >= #{startCreatedDate}
AND CREATED_AT < DATE_ADD(#{endCreatedDate}, INTERVAL 1 DAY)
LIMIT #{start}, #{length}

SELECT USER_ID AS userId , USER_NAME AS userName , EMAIL AS email , CREATED_BY AS createdBy , DATE_FORMAT(CREATED_AT, '%Y-%m-%d %h:%i:%s') AS createdAt FROM USER
AND USER_ID = #{userId}
AND EMAIL = #{email}

mapper.insert:
INSERT INTO USER (
      USER_NAME
    , PASSWORD
    , EMAIL
    , CREATED_BY
) VALUES (
      #{userName}
    , #{password}
    , #{email}
    , #{createdBy}
)
